mapper.select:
SELECT o.id, o.customer_id customerId, c.name customerName, o.workshop_id workshopId, w.name workshopName, o.stock_id stockId, s.name stockName, o.type, o.num, o.price, IF(o.type='退货', SUM(o.num*o.price*-1),SUM(o.num*o.price)) totalPrice, o.remarks, o.order_date orderDate, o.create_time createTime, o.status FROM order_info o LEFT JOIN stock s ON o.stock_id = s.id LEFT JOIN customer c ON o.customer_id = c.id LEFT JOIN workshop w ON o.workshop_id = w.id
o.status = '1'
AND o.type = #{type, jdbcType=VARCHAR}
AND o.customer_id = #{customerId, jdbcType=INTEGER}
AND o.order_date = #{orderDate, jdbcType=VARCHAR}
AND o.order_date >= #{startTime, jdbcType=VARCHAR}
AND o.order_date <= #{endTime, jdbcType=VARCHAR}
AND ( s.name LIKE CONCAT (#{key, jdbcType=VARCHAR}, '%') OR c.name LIKE CONCAT (#{key, jdbcType=VARCHAR}, '%') OR w.name LIKE CONCAT (#{key, jdbcType=VARCHAR}, '%') )
AND w.workshop_id = #{workshopId, jdbcType=INTEGER}
AND o.workshop_id IS NULL
GROUP BY o.id

SELECT date_format(o.order_date,'%Y-%m') orderDate, IF(o.type='退货', SUM(o.num*o.price*-1),SUM(o.num*o.price)) totalPrice, c.name customerName, o.type, o.customer_id customerId, w.name workshopName, o.workshop_id workshopId FROM order_info o LEFT JOIN customer c ON o.customer_id = c.id LEFT JOIN workshop w ON o.workshop_id = w.id
o.status = '1'
AND o.customer_id = #{customerId, jdbcType=INTEGER}
AND o.order_date >= #{startTime, jdbcType=VARCHAR}
AND o.order_date <= #{endTime, jdbcType=VARCHAR}
AND c.name LIKE CONCAT (#{key, jdbcType=VARCHAR}, '%')
AND o.workshop_id IS NULL
AND o.workshop_id IS NOT NULL
GROUP BY orderDate, o.customer_id, o.type
,o.workshop_id
